// Helper to get questionnaire for age
export function getQuestionnaireForAge(ageMonths: number): { items: QuestionnaireItem[]; interval: AgeInterval } | null {
  const intervals: AgeInterval[] = [2, 4, 6, 8, 9, 10, 12, 14, 16, 18, 20, 22, 24, 27, 30, 33, 36, 42, 48, 54, 60];

  let selectedInterval: AgeInterval = 12;
  for (const interval of intervals) {
    if (ageMonths >= interval - 1) {
      selectedInterval = interval;
    } else {
      break;
    }
  }

  const items = QUESTIONNAIRES_BY_AGE[selectedInterval];
  if (!items || items.length === 0) {
    // Find closest available questionnaire
    const availableIntervals = Object.keys(QUESTIONNAIRES_BY_AGE).map(Number) as AgeInterval[];
    if (availableIntervals.length === 0) return null;

    const closest = availableIntervals.reduce((prev, curr) =>
      Math.abs(curr - ageMonths) < Math.abs(prev - ageMonths) ? curr : prev
    );

    return { items: QUESTIONNAIRES_BY_AGE[closest] || [], interval: closest };
  }

  return { items, interval: selectedInterval };
}
